import React, { useState } from "react";

function App() {
  const [inputText, setInputText] = useState("");
  const [items, setItems] = useState([]);
  function handleChange(event) {
    setInputText(event.target.value);
  }
  function addItem() {
    setItems((prevItems) => {
      return [...prevItems, inputText];
    });
    setInputText("");
  }

  let customStyle={
    textDecoration:"none"
  }

  function strikeItem(event){
    if(event.type.checked)
      customStyle.textDecoration="line-through";
  }

  return (
      <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input type="text" onChange={handleChange} value={inputText} placeholder="Write Here" />
        <button onClick={addItem}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {items.map((todoItem,index) => (
              <li key={index}> {todoItem} <input onClick={strikeItem} type="checkbox"></input> </li>
          ))}
        </ul>
      </div>
    </div>


  );
}

export default App;
